import React from "react";
import { Button, Table, Form, Modal } from "react-bootstrap";


class Pessoa extends React.Component{

    constructor(props){
        super(props);

        this.state={
            id: 0,
            nome : '',
            email : '',
            telefone : '',
            aniversario : '',
            identificacao : '',
            pessoas: [],
            modalAberta: false
        }

        this.deletarPessoa = this.deletarPessoa.bind(this);
        this.buscarPessoas = this.buscarPessoas.bind(this);
        this.cadastrarPessoa = this.cadastrarPessoa.bind(this);
        this.atualizarNome = this.atualizarNome.bind(this);
        this.atualizarEmail = this.atualizarEmail.bind(this);
        this.atualizarTelefone = this.atualizarTelefone.bind(this);
        this.atualizarAniversario = this.atualizarAniversario.bind(this);
        this.atualizarIdentificacao = this.atualizarIdentificacao.bind(this);
        this.submit = this.submit.bind(this);
        this.fecharModal = this.fecharModal.bind(this);
        this.abrirModal = this.abrirModal.bind(this);

    }

    componentDidMount(){
        this.buscarPessoa();
    }

    componentWillUnmount(){

    }

    buscarPessoas = () => {
        const token = localStorage.getItem('token');

        fetch("http://localhost.com:8080/pessoa", {
            method: 'GET',
            header: {
                'Authorization': `Bearer ${token}`,
                'Content-Type':'application/json'},
        })
        .then(resposta => resposta.json)
        .then(dados => this.setState({ pessoa : dados }))
    }

    buscarPessoa = (id) => {
        const token = localStorage.getItem('token');

        fetch("http://localhost.com:8080/pessoa", {
            method: 'GET',
            header: {
                'Authorization': `Bearer ${token}`,
                'Content-Type':'application/json'},
        })
        .then(resposta => resposta.json)
        .then(dados => this.setState({
            id: dados.id,
            nome : dados.nome,
            email : dados.email,
            telefone : dados.telefone,
            aniversario : dados.aniversario,
            identificacao : dados.identificacao
        }))
    }

    cadastrarPessoa = (pessoa) => {
        const token = localStorage.getItem('token');

        fetch("http://localhost.com:8080/pessoa",{
            method: 'POST',
            header: {
                'Authorization': `Bearer ${token}`,
                'Content-Type':'application/json'},
            body: JSON.stringify(pessoa)
        })
        .then(resposta => {
            if(resposta.ok){
                this.buscarPessoas();
                this.fecharModal();
            }else{
                alert('Não foi possível adicionar a pessoa!');
            }
        })
    }

    atualizarPessoa = (pessoa) => {
        const token = localStorage.getItem('token');

        fetch("http://localhost.com:8080/pessoa/"+pessoa.id,{
            method: 'PUT',
            header: {
                'Authorization': `Bearer ${token}`,
                'Content-Type':'application/json'},
            body: JSON.stringify(pessoa)
        })
        .then(resposta => {
            if(resposta.ok){
                this.buscarPessoas();
                this.fecharModal();
            }else{
                alert('Não foi possível atualizar a pessoa!');
            }
        })
    }

    deletarPessoa = (id) =>{
        const token = localStorage.getItem('token');

        fetch("http://localhost.com:8080/pessoa/"+id, {
            method: 'DELETE',
            header: {
                'Authorization': `Bearer ${token}`,
                'Content-Type':'application/json'}        
            })
        .then(resposta => {
            if(resposta.ok){
                this.buscarPessoas();
                this.fecharModal();
            }else{
                alert('Pessoa excluída');
            }
        })
    }

    carregarDados = (id) =>{
        const token = localStorage.getItem('token');

        fetch("http://localhost.com:8080/pessoa/"+id, {
            method: 'GET',
            header: {
                'Authorization': `Bearer ${token}`,
                'Content-Type':'application/json'}        
            })
        .then(resposta => resposta.json)
        .then(pessoa => {
            this.setState({
                id: pessoa.id,
                nome : pessoa.nome,
                email : pessoa.email,
                telefone : pessoa.telefone,
                aniversario : pessoa.aniversario,
                identificacao : pessoa.identificacao
            })
        this.abrirModal();
        })
        .catch(error => {
            console.error("Erro na requisição:", error);
        });
    }

    atualizarNome = (e) => {
        this.setState({
            nome: e.target.value
        })
    }

    atualizarEmail = (e) => {
        this.setState({
            email: e.target.value
        })
    }

    atualizarTelefone = (e) => {
        this.setState({
            telefone: e.target.value
        })
    }

    atualizarAniversario = (e) => {
        this.setState({
            aniversario: e.target.value
        })
    }

    atualizarIdentificacao = (e) => {
        this.setState({
            identificacao: e.target.value
        })
    }

    abrirModal = () => {
        this.setState({
            modalAberta: true
        })
    }

    abrirModalAtualizar = (id) => {
        this.setState({
            id: id,
            modalAberta: true
        })
    }

    fecharModal = () => {
        this.setState({
            id: 0,
            nome: "",
            email: "",
            telefone : "",
            aniversario : "",
            identificacao : "",
            modalAberta: false
        })
    }

    submit(){

        const pessoa = {
        id: this.state.id,
        nome: this.state.nome,
        email: this.state.email,
        telefone : this.state.telefone,
        aniversario : this.state.aniversario,
        identificacao : this.state.identificacao
        }

        if(this.state.id === 0){
            this.cadastrarPessoa(pessoa);
        }else{
            this.atualizarPessoa(pessoa);
        }
    }

    reset = () => {
        this.setState({
            id: 0,
            nome: '',
            email: '',
            telefone : '',
            aniversario : '',
            identificacao : ''
        })
        this.abrirModal();
    }

    renderModal(){
        return(
            <Modal show={this.state.modalAberta} onHide={this.fecharModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Preencha os dados da pessoa</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <Form id="modalForm" onSubmit={this.submit}>
                        <Form.Group>
                            <Form.Label>ID</Form.Label>
                            <Form.Control type="text" value={this.state.id} readOnly={true}/>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>Nome</Form.Label>
                            <Form.Control type="text" placeholder="Nome da pessoa" value={this.state.nome} onChange={this.atualizarNome}/>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>E-mail</Form.Label>
                            <Form.Control type="email" placeholder="Email" value={this.state.email} onChange={this.atualizarEmail}/>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>Telefone</Form.Label>
                            <Form.Control type="text" placeholder="Telefone" value={this.state.telefone} onChange={this.atualizarTelefone}/>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>Data de nascimento</Form.Label>
                            <Form.Control type="date" placeholder="Aniversário" value={this.state.aniversario} onChange={this.atualizarAniversario}/>
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>Identificação</Form.Label>
                            <Form.Select type="form-control" value={this.state.identificacao} onChange={this.atualizarIdentificacao}>
                            <option value="Cliente">Cliente</option>
                            <option value="Empresa">Empresa de Turismo</option>
                            <option value="Administrador">Administrador</option>
                            </Form.Select>
                        </Form.Group>
                    </Form>
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="primary" type="submit" onClick={this.submit}>Confirmar</Button>
                    <Button variant="secondary" onClick={this.fecharModal}>Cancelar</Button>
                </Modal.Footer>
            </Modal>
            );
        }

        renderTabela(){
            return (
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Email</th>
                        <th>Telefone</th>
                        <th>Aniversário</th>
                        <th>Identificacao</th>
                        <th>Opções</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        this.state.pessoas.map((pessoa) => (
                        <tr>
                            <td>{pessoa.nome}</td>
                            <td>{pessoa.email}</td>
                            <td>{pessoa.telefone}</td>
                            <td>{pessoa.aniversario}</td>
                            <td>{pessoa.identificacao}</td>
                            <td>
                                <div>
                                    <Button variant="link" onClick={() => this.abrirModalAtualizar(pessoa.id)}>Atualizar</Button>
                                    <Button variant="link" onClick={() => this.deletarPessoa(pessoa.id)}>Excluir</Button>
                                </div>
                            </td>
                        </tr>    
                        ))}
                </tbody>
            </Table>
            );
        }

        render(){
            return(
                <div>
                    <br />
                        <Button variant="warning" type="submit" onClick={this.reset}>Adicionar pessoa</Button>
                        {this.renderTabela()}
                        {this.renderModal()}
                </div>
            );
        }


}

export default Pessoa;
